		addEvent: function( obj, evt, fn, useCapture )
		{
			if (obj.addEventListener)
			{
				obj.addEventListener(evt, fn, Boolean( useCapture ));
				return true;
			}
			else if (obj.attachEvent)
			{
				var r = obj.attachEvent("on"+evt, fn);
				
				return r;
			}
			else
			{
				return false;
			}
		},
	
